import { onMount } from 'svelte';
  import { Search, X, MoreVertical, SkipBack, SkipForward, Play, Pause, Key } from 'lucide-svelte';

  let player;
  let currentTime = 0;
  let duration = 0;
  let isPlaying = false;
  let searchQuery = '';
  let showSearchModal = false;
  let showApiKeyModal = false;
  let searchResults = [];
  let displayedResults = 5;
  let playlist = [];
  let currentVideoId = '';
  let isSearching = false;
  let youtubeApiKey = '';
  let showPlaylist = true;
  let volume = 50;
  let playerError = null;

  onMount(() => {
    loadYouTubeAPI();
  });

  function loadYouTubeAPI() {
    const tag = document.createElement('script');
    tag.src = "https://www.youtube.com/iframe_api";
    const firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

    window.onYouTubeIframeAPIReady = () => {
      player = new YT.Player('youtube-player', {
        height: '0',
        width: '0',
        videoId: '',
        events: {
          'onReady': onPlayerReady,
          'onStateChange': onPlayerStateChange,
          'onError': onPlayerError
        }
      });
    };
  }

  function onPlayerReady(event) {
    // Player is ready
  }

  function onPlayerStateChange(event) {
    if (event.data == YT.PlayerState.PLAYING) {
      isPlaying = true;
      updateTimeInfo();
    } else if (event.data == YT.PlayerState.PAUSED) {
      isPlaying = false;
    }
  }

  function onPlayerError(event) {
    console.error('YouTube player error:', event.data);
    playerError = `Error: ${event.data}`;
    isPlaying = false;
  }

  function updateTimeInfo() {
    if (player && player.getCurrentTime) {
      currentTime = player.getCurrentTime();
      duration = player.getDuration();
      if (isPlaying) {
        setTimeout(updateTimeInfo, 1000);
      }
    }
  }

  async function searchYouTube() {
    if (!youtubeApiKey) {
      alert("Please enter a YouTube API key first");
      showApiKeyModal = true;
      return;
    }

    if (!searchQuery.trim()) {
      alert("Please enter a search query");
      return;
    }

    isSearching = true;
    try {
      const response = await fetch(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(searchQuery)}&type=video&key=${youtubeApiKey}&maxResults=25`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      searchResults = data.items.map(item => ({
        title: item.snippet.title,
        artist: item.snippet.channelTitle,
        videoId: item.id.videoId
      }));
    } catch (error) {
      console.error('Error fetching YouTube data:', error);
      alert('An error occurred while searching. Please try again.');
      searchResults = [];
    } finally {
      isSearching = false;
    }
  }

  function loadMore() {
    displayedResults += 5;
  }

  function addToPlaylist(track) {
    playlist = [...playlist, track];
    showSearchModal = false;
  }

  function removeFromPlaylist(index) {
    playlist = playlist.filter((_, i) => i !== index);
  }

  function playVideo(videoId) {
    if (player && player.loadVideoById) {
      playerError = null;
      try {
        player.loadVideoById(videoId);
        currentVideoId = videoId;
        isPlaying = true;
      } catch (error) {
        console.error('Error loading video:', error);
        playerError = `Error loading video: ${error.message}`;
        isPlaying = false;
      }
    }
  }

  function togglePlay() {
    if (player) {
      if (isPlaying) {
        player.pauseVideo();
        isPlaying = false;
      } else {
        if (currentVideoId) {
          player.playVideo();
          isPlaying = true;
        } else if (playlist.length > 0) {
          playVideo(playlist[0].videoId);
        }
      }
    }
  }

  function formatTime(seconds) {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  }

  function handleKeydown(event, action) {
    if (event.key === 'Enter' || event.key === ' ') {
      action();
      event.preventDefault();
    }
  }

  function setApiKey() {
    if (youtubeApiKey.trim()) {
      showApiKeyModal = false;
    } else {
      alert("Please enter a valid API key");
    }
  }

  function setVolume() {
    if (player && player.setVolume) {
      player.setVolume(volume);
    }
  }

  $: if (player && player.setVolume) {
    player.setVolume(volume);
  }

